<?phpclass Admin_Model_Motoristas extends Zend_Db_Table {    protected $_name = 'motoristas';    protected $_primary = 'id';    public function init() {        $viewRenderer = Zend_Controller_Action_HelperBroker::getStaticHelper('ViewRenderer');        $viewRenderer->init();        $this->view = $viewRenderer->view;    }    public function insert(array $data) {        if (!is_array($data))            return false;        $auth = Zend_Auth::getInstance();        $user = $auth->getIdentity();        $data['user'] = $user->id;        if (!empty($data['data_nascimento'])) {            $data['data_nascimento'] = $this->view->ConvercaoDate('/', $data['data_nascimento'], 3);        }        if (is_numeric($data['id'])) {            $this->update($data, "id = " . $data['id']);            return $data['id'];        }        unset($data['id']);        $info = $this->info();        $data_insert = array_intersect_key($data, $info['metadata']);        parent::insert($data_insert);    }    public function Dados() {        $sql = $this->select();        if ($result = $this->fetchRow($sql)) {            return $result;        }    }    public function ListarMotoristas($empresa = false) {        $sql = $this->select()                ->where("ativo = ?", 1)                ->order("nome");        if ($empresa) {            $sql->where("atua_empresa = ?", 1);        }        if ($categorias = $this->fetchAll($sql)) {            return $categorias->toArray();        } else {            return FALSE;        }    }    public function GetDados($id) {        $sql = $this->select()                ->where("id = ?", $id);        if ($data = $this->fetchRow($sql)) {            $data['data_nascimento'] = $this->view->ConvercaoDate('-', $data['data_nascimento'], 2);            return $data->toArray();        } else {            return FALSE;        }    }    public function ConsultarCPF($cpf) {        $sql = $this->select()                ->setIntegrityCheck(false)                ->from(array('m' => $this->_name), array("id"))                ->where("m.atua_empresa = 1")                ->where("m.ativo = 1")                ->where("m.cpf = ?", $cpf);        if ($result = $this->fetchRow($sql)) {            return $result->id;        } else {            return false;        }    }    public function update(array $data, $where) {        $info = $this->info();        $data_insert = array_intersect_key($data, $info['metadata']);        parent::update($data_insert, $where);    }    public function Lista() {        $sql = $this->select()                ->order("id");        if ($result = $this->fetchAll($sql)) {            return $result->toArray();        }    }}